import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    cartList: [],
    total: 0,
  },
  reducers: {
    add(status, action) {
      return {...status}
    },
    remove(status, action) {},
  },
});
export const { add, remove } = cartSlice.actions;
export const cartReducer = cartSlice.reducer;
